import React from "react"
import axios from "axios"
import { baseUrl, formatNumber, authorization } from "../config.js";
import domToPdf from "dom-to-pdf";
import logo from "../components/asset/logo.png"
// import { FaDownload } from "react-icons/fa"
// import { BsFillMenuButtonWideFill } from "react-icons/bs"
// import { CgNotes } from "react-icons/cg"
// import { MdDelete } from "react-icons/md"
// import { BiSearchAlt2 } from "react-icons/bi"

export default class Transaksi extends React.Component {
    constructor() {
        super()
        this.state = {
            transaksi: [],
            visible: "",
            user: "",
            id: "",
            outlet: [],
            masterTransaksis: [],
            masterOutlets: [],
            nama_outlet: ""

        }

        if (!localStorage.getItem("token")) {
            window.location.href = "/auth"
        }
    }

    getData() {
        let endpoint = `${baseUrl}/transaksi`
        axios.get(endpoint, authorization)
            .then(response => {
                let dataTransaksi = response.data
                for (let i = 0; i < dataTransaksi.length; i++) {
                    let total = 0;
                    for (let j = 0; j < dataTransaksi[i].detail_transaksi.length; j++) {
                        let harga = dataTransaksi[i].detail_transaksi[j].paket.harga
                        let qty = dataTransaksi[i].detail_transaksi[j].qty

                        total += (harga * qty)

                        // tambahkan key total
                        dataTransaksi[i].total = total
                    }
                }

                this.setState({ transaksi: dataTransaksi })
                this.setState({ masterTransaksis: response.data })
            })
            .catch(error => console.log(error))
    }

    getOutlet() {
        let endpoint = `${baseUrl}/outlet`
        axios.get(endpoint, authorization)
            .then(response => {
                this.setState({ outlets: response.data })
            })
            .catch(error => console.log(error))
    }

    componentDidMount() {
        this.getData()
        let user = JSON.parse(localStorage.getItem("user"))
        this.getOutlet()

        // cara kedua
        if (user.role === 'Admin' || user.role === 'Kasir') {
            this.setState({
                visible: true
            })
        } else {
            this.setState({
                visible: false
            })
        }
    }

    convertStatus(id_transaksi, status) {
        if (status === 1) {
            return (
                <div className="badge bg-info">
                    Transaksi Baru
                    <br />

                    <a onClick={() => this.changeStatus(id_transaksi, 2)} className="text-danger">
                        Klik disini untuk mengganti level
                    </a>
                </div>
            )
        } else if (status === 2) {
            return (
                <div className="badge bg-warning">
                    Sedang diproses

                    <br />

                    <a onClick={() => this.changeStatus(id_transaksi, 3)} className="text-danger">
                        Klik disini untuk mengganti level
                    </a>
                </div>
            )
        } else if (status === 3) {
            return (
                <div className="badge bg-secondary">
                    Siap Diambil

                    <br />

                    <a onClick={() => this.changeStatus(id_transaksi, 4)} className="text-danger">
                        Klik disini untuk mengganti level
                    </a>
                </div>
            )
        } else if (status === 4) {
            return (
                <div className="badge bg-success">
                    Telah Diambil
                </div>
            )
        }
    }

    changeStatus(id, status) {
        if (window.confirm(`Apakah Anda yakin ingin mengganti status transaksi ini?`)) {
            let endpoint = `${baseUrl}/transaksi/status/${id}`
            let data = {
                status: status
            }

            axios
                .post(endpoint, data, authorization)
                .then(response => {
                    window.alert(`Status transaksi telah diubah`)
                    this.getData()
                })
                .catch(error => console.log(error))
        }
    }

    deleteTransaksi(id) {
        if (window.confirm(`Apakah Anda yakin ingin menghapus transaksi ini ?`)) {
            let endpoint = `${baseUrl}/transaksi/${id}`
            axios.delete(endpoint, authorization)
                .then(response => {
                    window.alert(response.data.message)
                    this.getData()
                })
                .catch(error => console.log(error))
        }

    }

    convertStatusBayar(id_transaksi, dibayar) {
        if (dibayar === 0) {
            return (
                <div className="badge bg-danger text-white">
                    Belum Dibayar

                    <br />

                    <a className="text-primary"
                        onClick={() => this.changeStatusBayar(id_transaksi, 1)}>
                        Kilik disini untuk mengganti status
                    </a>
                </div>
            )
        } else if (dibayar === 1) {
            return (
                <div className="badge bg-success text-white">
                    Sudah Dibayar
                </div>
            )
        }
    }

    changeStatusBayar(id, status) {
        if (window.confirm(`Apakah Anda yakin ingin mengubah status pembayaran ini?`)) {
            let endpoint = `${baseUrl}/transaksi/bayar/${id}`
            axios.get(endpoint, authorization)
                .then(response => {
                    window.alert(`Status pembayaran telah diubah`)
                    this.getData()
                })
                .catch(error => console.log(error))
        }
    }

    convertTime = tgl => {
        let date = new Date(tgl)
        return `${date.getDate()}/${Number(date.getMonth()) + 1}/${date.getFullYear()}`
    }

    convertPdf() {
        // ambil element yang akan diconvert ke pdf
        let element = document.getElementById(`target`)
        let options = {
            filename: "Rincian Data Transaksi.pdf"
        }

        domToPdf(element, options, () => {
            window.alert("file will download soon")
        })
    }

    printStruk(id) {
        var element = document.getElementById(`struk${id}`);
        var options = {
            filename: `Invoice-${id}.pdf`
        };
        domToPdf(element, options, function (pdf) {
            window.alert(`Invoice will download soon`)
        });
    }

    searching(ev) {
        let code = ev.keyCode;
        if (code === 13) {
            let data = this.state.masterTransaksis;
            let found = data.filter(it =>
                it.outlet.nama_outlet.toLowerCase().includes(this.state.search.toLowerCase()) ||
                it.member.toLowerCase().includes(this.state.search.toLowerCase())

            )
            this.setState({ transaksi: found });
        }
    }

    convertStatusPrint(id_transaksi, status) {
        if (status === 1) {
            return (
                <div >
                    Transaksi Baru

                </div>
            )
        } else if (status === 2) {
            return (
                <div >
                    Sedang diproses
                </div>
            )
        } else if (status === 3) {
            return (
                <div>
                    Siap Diambil
                </div>
            )
        } else if (status === 4) {
            return (
                <div >
                    Telah Diambil
                </div>
            )
        }
    }

    render() {
        const target = React.createRef()
        const optionPDF = {
            orientation: `landscape`,
            unit: `cm`,
            format: [21, 29.7]
        }
        return (
            <div className="container">
                <div className="card">
                    <div ref={target} id="target">
                        <div className="card-header bg-secondary">
                            <h4 className="text-center text-white">
                                Data Transaksi &nbsp;
                                {/* <BsFillMenuButtonWideFill size={25} color="white" /> */}
                            </h4>
                        </div>
                        <div className="col-sm-4 my-2">
                            <div class="d-flex">
                                {/* <BiSearchAlt2 style={{ marginLeft: "1rem", marginTop: "0.5rem", position: "absolute" }} color="#808080" size="1.5em" /> */}
                                {/* &nbsp;  &nbsp; &nbsp; 
                                <input class="form-control me-2 px-5" type="search" placeholder="Search" aria-label="Search"
                                    value={this.state.search} onChange={ev => this.setState({ search: ev.target.value })} onKeyUp={(ev) => this.searching(ev)} /> */}
                            </div>

                            
                        </div>
                        <div className="card-body">
                            <h5 className="white" style={{ float: "left" }}>
                                <button className="btn btn-sm btn-success" onClick={() => this.convertPdf()}>
                                    Print PDF &nbsp;
                                    <i class="bi bi-download"></i>
                                </button>
                                
                                {/* <FaDownload size={27} color="#696969"
                                    onClick={() => this.convertPdf()}></FaDownload> */}
                            </h5>
                            <br></br>
                            <br></br>
                            <ul className="list-group">
                                {this.state.transaksi.map(trans => (
                                    <li className="list-group-item">
                                        <h3>Transaksi</h3>
                                        <div className="row">
                                            {/* this is member area */}
                                            <div className="col-lg-3">
                                                <small className="text-primary">
                                                    Member
                                                </small> <br />
                                                {trans.member.nama}
                                            </div>


                                            {/* this is outlet area */}
                                            <div className="col-lg-3">
                                                <small className="text-primary">
                                                    Outlet
                                                </small> <br />
                                                {trans.user.outlet.nama_outlet}
                                            </div>

                                            {/* this is resi area */}
                                            <div className="col-lg-3">
                                                <small className="text-primary">
                                                    Kode Invoice
                                                </small> <br />
                                                {trans.resi}
                                            </div>

                                            {/* this is tgl transaksi area  */}
                                            <div className="col-lg-3">
                                                <small className="text-primary">
                                                    Tanggal Transaksi
                                                </small> <br />
                                                {this.convertTime(trans.tgl)}
                                            </div>

                                            {/* this is batas waktu area  */}
                                            <div className="col-lg-3">
                                                <small className="text-info">
                                                    Batas Waktu
                                                </small> <br />
                                                {this.convertTime(trans.batas_waktu)}
                                            </div>

                                            {/* this is tanggal bayar area  */}
                                            <div className="col-lg-3">
                                                <small className="text-info">
                                                    Tanggal Bayar
                                                </small> <br />
                                                {this.convertTime(trans.tgl_bayar)}
                                            </div>

                                            {/* this is status area  */}
                                            <div className="col-lg-3">
                                                <small className="text-info">
                                                    Status
                                                </small> <br />
                                                {this.convertStatus(trans.id_transaksi, trans.status)}
                                            </div>

                                            {/* this is status pembayaran  */}
                                            <div className="col-lg-3">
                                                <small className="text-info">
                                                    Status Pembayaran
                                                </small> <br />
                                                {this.convertStatusBayar(trans.id_transaksi, trans.dibayar)}
                                            </div>

                                            <div className="col-lg-3">
                                                <small className="text-info">
                                                    Total
                                                </small><br />
                                                Rp {formatNumber(trans.total)}
                                            </div>

                                            {/* this is struk area */}
                                            <div className="col-lg-3">
                                                <small className="text-info">
                                                    Struk
                                                </small><br />
                                                <button className="btn btn-warning btn-sm"
                                                    onClick={() => this.printStruk(trans.id_transaksi)}>
                                                        <i class="bi bi-receipt"></i>
                                                </button>
                                            </div>

                                            <div style={{ display: `none` }}>
                                                <div className="col-lg-12 p-3 mb-2"
                                                    id={`struk${trans.id_transaksi}`} style={{margin: '4rem'}}>
                                                        <center><img src={logo} className="img-print" /></center><br />
                                                    {/* <h3 className="text-info1 text-center">
                                                        D-Laundry
                                                    </h3> */}
                                                    <h5 className="text-center">
                                                        Clean and Fast
                                                    </h5>

                                                    <h4>Kode Invoice      : {trans.resi}</h4>
                                                    <h4>Member       : {trans.member.nama}</h4>
                                                    <h4>Nama Petugas : {trans.user.nama}</h4>
                                                    <h4>Outlet       : {trans.user.outlet.nama_outlet}</h4>
                                                    <h4>Alamat       : {trans.user.outlet.alamat}</h4>
                                                    <h4>Tgl          : {this.convertTime(trans.tgl)}</h4>
                                                    <hr />
                                                    <h4>Status Laundry : {this.convertStatusPrint(trans.id_transaksi, trans.status)}</h4><hr />


                                                    <div className="row mt-3"
                                                        style={{ borderBottom: `1px dotted black` , font: `font-weight`}}>
                                                        <div className="col-4">
                                                            <h4>Paket</h4>
                                                        </div>
                                                        <div className="col-2">
                                                            <h4>Qty</h4>
                                                        </div>
                                                        <div className="col-3">
                                                            <h4>Harga Satuan</h4>
                                                        </div>
                                                        <div className="col-3">
                                                            <h4>Total</h4>
                                                        </div>
                                                    </div>

                                                    {trans.detail_transaksi.map(item => (
                                                        <div className="row mt-3"
                                                            style={{ borderBottom: `1px dotted black` }}>
                                                            <div className="col-4">
                                                                <h5>{item.paket.jenis_paket}</h5>
                                                            </div>
                                                            <div className="col-2">
                                                                <h5>{item.qty}</h5>
                                                            </div>
                                                            <div className="col-3">
                                                                <h5>Rp {formatNumber(item.paket.harga)}</h5>
                                                            </div>
                                                            <div className="col-3">
                                                                <h5>Rp {formatNumber(item.paket.harga * item.qty)}</h5>
                                                            </div>
                                                        </div>
                                                    ))}

                                                    <div className="row mt-2">
                                                        <div className="col-lg-4" style={{alignItems: 'right'}}>
                                                        <br />

                                                            <h4>Total Bayar :  Rp {formatNumber(trans.total)}</h4>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="col-lg-3">
                                                <small className={`text-info ${this.state.visible ? `` : `d-none`}`}>
                                                    Option
                                                </small><br />
                                                <button className={`btn btn-sm btn-danger ${this.state.visible ? `` : `d-none`}`}
                                                    onClick={() => this.deleteTransaksi(trans.id_transaksi)}>
                                                        <i class="bi bi-trash"></i>
                                                    {/* <MdDelete size={20} color="white" /> */}
                                                </button>
                                            </div>
                                        </div> <hr />

                                        {/* area detail transaksi */}
                                        <h6>Detail Transaksi</h6>
                                        {trans.detail_transaksi.map(detail => (
                                            <div className="row">
                                                {/* area nama paket col-3 */}
                                                <div className="col-lg-3">
                                                    {detail.paket.jenis_paket}
                                                </div>
                                                {/* area quantity col-2*/}
                                                <div className="col-lg-3">
                                                    Qty: {detail.qty}
                                                </div>
                                                {/* area harga paket col-3*/}
                                                <div className="col-lg-3">
                                                    @ Rp {formatNumber(detail.paket.harga)}
                                                </div>
                                                {/* area harga total col-4  */}
                                                <div className="col-lg-3">
                                                    Rp {formatNumber(detail.paket.harga * detail.qty)}
                                                </div>
                                            </div>
                                        ))}<br></br><br></br><br></br>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
